import { useRef } from 'react'
import '../styles/canvas.scss'

export const Canvas = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null)

  return <div className="canvas">
    <canvas ref={canvasRef} width={600} height={400}/>
  </div>
}
