import { useEffect, useRef } from 'react'
import '../styles/canvas.scss'
import { useActions } from './../hooks/useActions'
import Brush from './../tools/brush'
import { MyModal } from './Modal'
import { useParams } from 'react-router-dom'
import { useTypedSelector } from './../hooks/useTypedSelector';

export const Canvas = () => {
  const {setCanvas, setTool, pushToUndo} = useActions()
  const canvasRef = useRef<HTMLCanvasElement | null>(null)
  const params = useParams()
  const username = useTypedSelector(state => state.canvas.username)

  useEffect(() => {
    if (canvasRef.current) {
      setCanvas(canvasRef.current)
      setTool(new Brush(canvasRef.current))
    }
  }, [])

  useEffect(() => {
    if (username) {
      const soket = new WebSocket("ws://localhost:5000")
      soket.onopen = () => {
        console.log('подключение установлено, ((внутри сокет онопен)')
        soket.send(JSON.stringify({
          id: params.id,
          username: username,
          method: "connection"
        }))
        
      }
    }
  }, [username])
  
  return <div className="canvas">
    <MyModal />
    <canvas onMouseDown={() => pushToUndo(canvasRef.current?.toDataURL() as string)} ref={canvasRef} width={600} height={400}/>
  </div>
}
