import Brush from './brush'

export default class Eraser extends Brush {
  mouseDown = false

  constructor(canvas: HTMLCanvasElement) {
    super(canvas)
  }

  draw(x, y) {
    this.ctx.strokeStyle = "white"
    this.ctx.lineTo(x, y)
    this.ctx.stroke()
  }

}
